    overflow: hidden;

    @media (max-width: 1200px) {
        width: 100%;
        max-height: 500px;
        margin: 0;
    }

    @media (max-width: 900px) {
        width: 100%;
        max-height: 420px;
        margin: 0;
    }


    &-hamburger {
        z-index: 10;
        width: 50px;
        height: 50px;
        background-color: color(primary);
        position: absolute;
        right: 2.5rem;
        top: 2.5rem;
        border-radius: 50%;
        animation: 1.2s ease-out 1s 1 onLoadDescriptionText;
        animation-fill-mode: both;

        @include icon-hovered-rotation;
    }

    &-arrow-right {
        z-index: 10;
        width: 50px;
        height: 50px;
        position: absolute;
        background-color: color(tertiary);
        bottom: 100px;
        animation: .5s ease-out .4s 1 slideInFromBottom;
        animation-fill-mode: both;
        @include arrow-hovered;

        @media (max-width: 1200px) {
            top: 450px;
            right: 0;
        }

        @media (max-width: 900px) {
            top: 370px;
            right: 0;
        }


        &-svg {
            margin-top: 50%;
            margin-left: 40%;
            transform: translateX(-40%) translateY(-50%) rotateZ(135deg) ;
        }
    }

    &-hero {
        width: 100%;
        height: 100%;
        background-size: cover;
        background-repeat: no-repeat;
        background-position: center;
        -webkit-animation: 1s ease-out 0s 1 zoomOutSlider;
        animation: 1s ease-out 0s 1 zoomOutSlider;


        &-backup {
            width: 100%;
            height: 100%;
            background-size: cover;
            background-repeat: no-repeat;
            background-position: center;
        }
    }
}

